const SOCKET_LIST = {};
const TILE_SIZE = 32;


const Entity = function(param) {
  let self = {
    x: 1300,
    y: 800,
    id: "",
    spdX: 0,
    spdY: 0,
    map: 'forest'
  }

  if (param) {
    if (param.x)
      self.x = param.x;
    if (param.y)
      self.y = param.y;
    if (param.map)
      self.map = param.map;
    if (param.id)
      self.id = param.id;
  }

  self.update = function() {
    self.updatePosition();
  }
  self.updatePosition = function() {
    self.x += self.spdX;
    self.y += self.spdY;
  }

  self.getDistance = function(pt) {
    return Math.sqrt(Math.pow(self.x - pt.x, 2) + Math.pow(self.y - pt.y, 2));
  }

  return self;
}

const Player = function(param) {
  let self = Entity(param);
  self.number = "" + Math.floor(50 * Math.random());
  self.goRight = false;
  self.goLeft = false;
  self.goUp = false;
  self.goDown = false;
  self.goAttack = false;
  self.mouseAngle = 0;
  self.maxSpd = 10;
  self.hp = 20;
  self.rateOfFire = 100;
  self.allowedToFire = true;
  self.maxHP = 20;
  self.score = 0;
  self.spriteCalc = 0;
  self.bulletAngle = 0;
  self.playername = param.playername;

  const super_update = self.update;

  self.update = function() {
    let oldX = self.x;
    let oldY = self.y;
    self.updateSpd();
    super_update();

    if (self.goAttack && self.allowedToFire) {
      self.shootBullet(self.bulletAngle);
      self.allowedToFire = false;
      setTimeout(() => { self.allowedToFire = true }, self.rateOfFire)
    }

    if (self.goRight || self.goDown || self.goLeft || self.goUp)
      self.spriteCalc += 0.2;
    if (self.x > 3200 - 20) {
      self.x = 3200 - 20;
    }
    if (self.y > 3200 - 40) {
      self.y = 3200 - 40;
    }
    if (self.x < 20) {
      self.x = 20;
    }
    if (self.y < 35) {
      self.y = 35;
    }

    // if(currentMap.isPositionWall(self)){
    //   self.x = oldX;
    //   self.y = oldY;
    // }
  }

  self.shootBullet = function(angle) {
    Bullet({
      parent: self.id,
      angle: angle,
      x: self.x,
      y: self.y,
      map: self.map
    });
  }

  self.updateSpd = function() {

    if (self.goRight) {
      self.spdX = self.maxSpd;
    } else if (self.goLeft) {
      self.spdX = -self.maxSpd;
    } else self.spdX = 0;

    if (self.goUp) {
      self.spdY = -self.maxSpd;
    } else if (self.goDown) {
      self.spdY = self.maxSpd;
    } else self.spdY = 0;
  }

  self.getInitPack = function() {
    return {
      id: self.id,
      x: self.x,
      y: self.y,
      number: self.number,
      hp: self.hp,
      maxHP: self.maxHP,
      score: self.score,
      map: self.map,
      mouseAngle: self.mouseAngle,
      spriteCalc: self.spriteCalc,
      bulletAngle: self.bulletAngle,
      playername: self.playername
    }
  }

  self.getUpdatePack = function() {
    return {
      id: self.id,
      x: self.x,
      y: self.y,
      hp: self.hp,
      score: self.score,
      map: self.map,
      mouseAngle: self.mouseAngle,
      spriteCalc: self.spriteCalc,
      bulletAngle: self.bulletAngle
    }
  }

  Player.list[self.id] = self;

  initPack.player.push(self.getInitPack());
  return self;
}

Player.list = {};
Player.onConnect = function(socket) {

  const player = Player({
    id: socket.id,
    playername: socket.playername,
    map: 'forest'
  });

  socket.on('keyPress', function(data) {
    if (data.inputId === 'left') {
      player.mouseAngle = 135;
      player.goLeft = data.state;
    } else if (data.inputId === 'right') {
      player.mouseAngle = 44;
      player.goRight = data.state;
    } else if (data.inputId === 'up') {
      player.mouseAngle = 225;
      player.goUp = data.state;
    } else if (data.inputId === 'down') {
      player.mouseAngle = 45;
      player.goDown = data.state;
    } else if (data.inputId === 'attack') {
      player.goAttack = data.state;
      player.mouseAngle = data.angle;
    } else if (data.inputId === 'mouseAngle')
        player.bulletAngle = data.state;
  });

  socket.on('sendMsgToServer', function(data) {
    let playerName = socket.playername;
    for (let i in SOCKET_LIST) {
      SOCKET_LIST[i].emit('addToChat', playerName + ': ' + data);
    }
  });

  socket.on('changeMap', function(data) {
    if (player.map === 'field') {
      player.map = 'forest';
    } else {
      player.map = 'field';
    }
  })

  socket.emit('init', {
    selfId: socket.id,
    player: Player.getAllInitPack(),
    bullet: Bullet.getAllInitPack()
  });
}

Player.getAllInitPack = function() {
  let players = [];
  for (let i in Player.list) {
    players.push(Player.list[i].getInitPack());
  }
  return players;
}

Player.onDisconnect = function(socket) {
  delete Player.list[socket.id];
  removePack.player.push(socket.id);
}

Player.makeCopy = function(player) {
  let copy = {
    id: player.id,
    x: player.x,
    y: player.y,
    hp: player.hp,
    score: player.score,
    map: player.map,
    mouseAngle: player.mouseAngle,
    spriteCalc: player.spriteCalc,
    bulletAngle: player.bulletAngle
  }
  return copy;
}

Player.updateAll = function() {
  const start = Date.now()
  let pack = [];
  for (let i in Player.list) {
    let player = Player.list[i];
    let playerOld = JSON.stringify(player);
    player.update();
    player.getUpdatePack();
    let copy = Player.makeCopy(player);
    let newPlayerUpdated = JSON.stringify(player);
    if (newPlayerUpdated !== playerOld) {
      pack.push(copy);
    }
  };

  // if(delta > 0) {
  //   console.log(`${delta}ms update`)
  // }
  return pack;
};


const Bullet = function(param) {
  let self = Entity(param);
  self.id = Math.floor(Math.random() * 1000);
  self.spdX = Math.floor(Math.cos(param.angle / 180 * Math.PI) * 50);
  self.spdY = Math.floor(Math.sin(param.angle / 180 * Math.PI) * 50);
  self.parent = param.parent;


  self.timer = 0;
  self.toRemove = false;
  let super_update = self.update;

  self.update = function() {
    if (self.timer++ > 15) {
      self.toRemove = true;
    }
    super_update();

    for (let i in Player.list) {
      let p = Player.list[i];
      if (self.map === p.map && self.getDistance(p) < 32 && self.parent !== p.id) {
        p.hp -= 1;

        if (p.hp <= 0) {
          let shooter = Player.list[self.parent];
          if (shooter) {
            shooter.score += 1;
          }
          p.hp = p.maxHP;
          p.x = Math.random() * 1000;
          p.y = Math.random() * 1000;

          self.toRemove = true;
        }
        for (let i in SOCKET_LIST) {
          let socket = SOCKET_LIST[i];
          let data = {
            player: [{
              id: p.id,
              hp: p.hp,
              x: p.x,
              y: p.y
            }]
          }
          socket.emit('update', BISON.encode(data));
          delete Bullet.list[self.id];
          removePack.bullet.push(self.id);
          socket.emit('remove', BISON.encode(removePack));
        }
      }
    }
  }

  self.getInitPack = function() {
    return {
      id: self.id,
      x: self.x,
      y: self.y,
      map: self.map
    }
  }

  self.getUpdatePack = function() {
    return {
      id: self.id,
      x: self.x,
      y: self.y
    }
  }

  Bullet.list[self.id] = self;
  initPack.bullet.push(self.getInitPack())
  return self;
}

Bullet.list = {};

Bullet.updateAll = function() {

  let pack = [];
  for (let i in Bullet.list) {
    let bullet = Bullet.list[i];
    bullet.update();
    // if(currentMap.isPositionWall(bullet)){
    //   bullet.toRemove = true;
    // }
    if (bullet.toRemove) {
      delete Bullet.list[i];
      removePack.bullet.push(bullet.id);
    } else {
      pack.push(bullet.getUpdatePack());
    }
  };
  return pack;
};

Bullet.getAllInitPack = function() {
  let bullets = [];
  for (let i in Bullet.list) {
    bullets.push(Bullet.list[i].getInitPack());
  }
  return bullets;
}

let initPack = { player: [], bullet: [], map: [] };

const Maps = function(id, imgSrc, grid) {
  let self = {
    id: id,
    image: imgSrc,
    width: grid[0].length * TILE_SIZE,
    height: grid.length * TILE_SIZE,
    grid: grid
  }

  self.isPositionWall = function(pt) {
    let gridX = Math.floor(pt.x / TILE_SIZE);
    let gridY = Math.floor(pt.y / TILE_SIZE);
    if (gridX < 0 || gridX >= self.grid[0].length || pt.x > 3200)
      return true;
    if (gridY < 0 || gridY >= self.grid.length || pt.y > 3200)
      return true;
    return self.grid[gridY][gridX];
  }

  self.getInitPack = function() {
    return {
      id: self.id,
      image: self.image,
      width: self.width,
      height: self.height,
      // grid: self.grid
    }
  }

  initPack.map.push(self.getInitPack());
  return self;
}
