io.sockets.on('connection', function(socket) {
  currentMap = Maps('field', '/client/img/map.png', array2D);
  socket.id = Math.floor(Math.random() * 1000);
  SOCKET_LIST[socket.id] = socket

  socket.on('signIn', function(data) {

    socket.playername = data.username;
    Player.onConnect(socket);
    socket.emit('signInResponse', { success: true });
  })

  //outputs on TERMINAL when a Player joins the game
  console.log('New player online');

  socket.on('evalServer', function(data) {
    if (!DEBUG) {
      return;
    } else {
      let res = eval(data);
      socket.emit('evalAnswer', res);
    }
  });


  socket.on('disconnect', function() {
    delete SOCKET_LIST[socket.id];
    Player.onDisconnect(socket);
  });

});

let removePack = { player: [], bullet: [] };
let updatePack = { player: [], bullet: [] };



setInterval(function() {
  let pack = {
    player: Player.updateAll(),
    bullet: Bullet.updateAll()
  }


  let jsonPack = BISON.encode(pack);
  let jsonRemovePack = BISON.encode(removePack);

  // console.log(pack);

  for (let i in SOCKET_LIST) {
    let socket = SOCKET_LIST[i];
    if (initPack.player.length > 0 || initPack.bullet.length > 0 || initPack.map.length > 0)
      socket.emit('init', initPack);
    if (pack.player.length > 0 || pack.bullet.length > 0) {
      socket.emit('update', jsonPack);
    }
    if (removePack.player.length > 0 || removePack.bullet.length > 0)
      socket.emit('remove', jsonRemovePack);
  }

  initPack.player = [];
  initPack.bullet = [];
  initPack.map = [];
  removePack.player = [];
  removePack.bullet = [];

}, 32);


